import React, { useContext } from 'react';
import { motion } from 'framer-motion';
import styled from 'styled-components';
import { Typography, Container, Box } from '@mui/material';
import { useInView } from 'react-intersection-observer';
import { ThemeContext } from '../context/ThemeContext';

const StyledSection = styled.section`
  padding-top: 20px;
  padding-bottom: 60px;
  background: ${props => props.theme.colors.background};
  overflow: hidden;
  min-height: 100vh;
  position: relative;
  transition: background-color 0.5s ease;
`;

const MainTitle = styled(motion.h2)`
  color: ${props => props.theme.colors.accent};
  font-size: clamp(40px, 5vw, 50px);
  font-weight: 700;
  margin-bottom: 80px;
  text-align: center;
  position: relative;
  width: 100%;
  transition: color 0.5s ease;

  &::after {
    content: '';
    position: absolute;
    bottom: -20px;
    left: 50%;
    transform: translateX(-50%);
    width: 150px;
    height: 2px;
    background: linear-gradient(to right, transparent, ${props => props.theme.colors.accent}, transparent);
    transition: background 0.5s ease;
  }
`;

const CategoryTitle = styled(motion.h3)`
  color: ${props => props.theme.colors.accent};
  font-size: 20px !important;
  font-weight: 500;
  margin-top: 40px;
  margin-bottom: 20px;
  text-align: left;
  position: relative;
  padding-left: 15px;
  line-height: 1;
  transition: color 0.5s ease;

  &::before {
    content: '';
    position: absolute;
    left: 0;
    top: 50%;
    transform: translateY(-50%);
    width: 5px;
    height: 20px;
    background: ${props => props.theme.colors.accent};
    border-radius: 2px;
    transition: background 0.5s ease;
  }
`;

const SkillsRow = styled(motion.div)`
  display: flex;
  white-space: nowrap;
  margin-bottom: 100px;
  padding: 40px 0;
  position: relative;
  background: linear-gradient(to right, transparent, 
    ${props => props.theme.isDarkMode 
      ? 'rgba(100, 255, 218, 0.02)' 
      : 'rgba(237, 109, 11, 0.02)'
    }, transparent);
  border-radius: 20px;
  overflow: hidden;
  transition: background 0.5s ease;

  &::before,
  &::after {
    content: '';
    position: absolute;
    left: 0;
    right: 0;
    height: 1px;
    transition: background 0.5s ease;
  }

  &::before {
    top: 0;
    background: linear-gradient(to right, transparent, 
      ${props => props.theme.isDarkMode 
        ? 'rgba(100, 255, 218, 0.1)' 
        : 'rgba(237, 109, 11, 0.1)'
      }, transparent);
  }

  &::after {
    bottom: 0;
    background: linear-gradient(to right, transparent, 
      ${props => props.theme.isDarkMode 
        ? 'rgba(100, 255, 218, 0.1)' 
        : 'rgba(237, 109, 11, 0.1)'
      }, transparent);
  }
`;

const SkillsTrack = styled(motion.div)`
  display: flex;
  gap: 4rem;
  padding: 0 2rem;
`;

const IconWrapper = styled(motion.div)`
  color: ${props => props.theme.colors.secondaryText};
  font-size: 20px;
  margin: 0 1.5rem;
  transition: all 0.3s cubic-bezier(0.645, 0.045, 0.355, 1);
  cursor: pointer;
  position: relative;
  padding: 15px;
  border-radius: 8px;
  background: ${props => props.theme.isDarkMode 
    ? 'rgba(255, 255, 255, 0.02)' 
    : 'rgba(0, 0, 0, 0.02)'
  };
  font-family: 'SF Mono', 'Fira Code', monospace;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.05);

  i {
    font-size: 35px;
    display: block;
    margin-bottom: 8px;
    text-align: center;
    transition: color 0.3s ease;
  }

  span.skill-name {
    display: block;
    font-size: 14px;
    text-align: center;
    opacity: 0.7;
    transition: opacity 0.3s ease;
  }

  &:hover {
    color: ${props => props.theme.colors.accent};
    transform: translateY(-8px) scale(1.05);
    background: ${props => props.theme.isDarkMode 
      ? 'rgba(237, 109, 11, 0.05)' 
      : 'rgba(237, 109, 11, 0.05)'
    };
    box-shadow: 0 10px 25px -10px rgba(0, 0, 0, 0.3);

    span.skill-name {
      opacity: 1;
    }
  }
`;

const CategorySection = styled(Box)`
  margin-bottom: 20px;
  margin-top: 80px;

  &:last-child {
    margin-bottom: 0;
  }
`;

// 3D lighting effect for category headers
const GlowingText = styled(motion.span)`
  display: inline-block;
  color: ${props => props.theme.colors.accent};
  text-shadow: 0 0 5px ${props => props.theme.colors.accent + '40'},
               0 0 10px ${props => props.theme.colors.accent + '20'};
  transition: color 0.5s ease, text-shadow 0.5s ease;
`;

const programming = [
  { iconClass: 'fas fa-code', name: 'C/C++' },
  { iconClass: 'fab fa-java', name: 'Java' },
  { iconClass: 'fab fa-js', name: 'JavaScript' },
  { iconClass: 'fas fa-layer-group', name: 'TypeScript' },
  { iconClass: 'fab fa-python', name: 'Python' },
  { iconClass: 'fab fa-html5', name: 'HTML' },
  { iconClass: 'fab fa-css3-alt', name: 'CSS' },
  { iconClass: 'fab fa-angular', name: 'Angular' },
  { iconClass: 'fab fa-react', name: 'React.js' },
  { iconClass: 'fab fa-node-js', name: 'Node.js' },
  { iconClass: 'fas fa-forward', name: 'Next.js' },
  { iconClass: 'fab fa-figma', name: 'Figma' },
  { iconClass: 'fas fa-icons', name: 'UI Design' },
];

const databases = [
  { iconClass: 'fas fa-database', name: 'MySQL' },
  { iconClass: 'fas fa-server', name: 'PostgreSQL' },
  { iconClass: 'fas fa-leaf', name: 'MongoDB' },
  { iconClass: 'fas fa-database', name: 'NoSQL' },
  { iconClass: 'fas fa-project-diagram', name: 'JIRA' },
  { iconClass: 'fas fa-book', name: 'Confluence' },
  { iconClass: 'fas fa-chart-bar', name: 'Power BI' },
];

const frameworks = [
  { iconClass: 'fab fa-cloud', name: 'Spring Boot' },
  { iconClass: 'fas fa-archive', name: 'Apache Maven' },
  { iconClass: 'fab fa-aws', name: 'AWS' },
  { iconClass: 'fas fa-cloud', name: 'Azure' },
  { iconClass: 'fas fa-users-cog', name: 'Agile' },
  { iconClass: 'fas fa-sync', name: 'CI/CD' },
  { iconClass: 'fab fa-docker', name: 'Docker' },
  { iconClass: 'fab fa-git-alt', name: 'Git' },
  { iconClass: 'fas fa-brain', name: 'YOLOv8' },
  { iconClass: 'fas fa-project-diagram', name: 'TensorFlow' },
  { iconClass: 'fas fa-robot', name: 'Ultralytics' },
  { iconClass: 'fas fa-chart-line', name: 'Matplotlib' },
  { iconClass: 'fas fa-square-root-alt', name: 'NumPy' }
];

function Skills() {
  const theme = useContext(ThemeContext);

  // Section heading animation
  const [titleRef, titleInView] = useInView({
    threshold: 0.1,
    triggerOnce: true,
  });

  // Category animations
  const [progRef, progInView] = useInView({
    threshold: 0.1,
    triggerOnce: true,
  });

  const [dbRef, dbInView] = useInView({
    threshold: 0.1,
    triggerOnce: true,
  });

  const [frameworkRef, frameworkInView] = useInView({
    threshold: 0.1,
    triggerOnce: true,
  });

  // Animation variants
  const titleVariants = {
    hidden: { opacity: 0, y: -20 },
    visible: { 
      opacity: 1, 
      y: 0,
      transition: { duration: 0.6 } 
    }
  };

  const categoryVariants = {
    hidden: { opacity: 0, x: -20 },
    visible: { 
      opacity: 1, 
      x: 0,
      transition: { duration: 0.5 }
    }
  };

  const rowVariants = {
    hidden: { opacity: 0, y: 30 },
    visible: { 
      opacity: 1, 
      y: 0,
      transition: { 
        duration: 0.8,
        staggerChildren: 0.1 
      } 
    }
  };

  // Create animated skill icons with scroll trigger
  const renderSkillRow = (skills, direction, ref, inView) => {
    // Define the animation direction and properties
    const baseX = direction === 'left' ? 100 : -100;

    // Custom animation for the skill track
    const trackAnimation = {
      x: [baseX, 0],
      transition: {
        duration: 1.5,
        ease: "easeOut"
      }
    };

    // Icon animation variants
    const iconVariants = {
      hidden: { opacity: 0, scale: 0.8, y: 20 },
      visible: { 
        opacity: 1, 
        scale: 1, 
        y: 0,
        transition: { 
          type: "spring",
          stiffness: 100,
          damping: 10 
        } 
      },
      hover: { 
        scale: 1.1, 
        y: -8,
        rotate: [-1, 1, -1],
        transition: { 
          duration: 0.3,
          rotate: {
            repeat: Infinity,
            duration: 1.5
          }
        } 
      }
    };

    return (
      <SkillsRow
        ref={ref}
        theme={theme}
        variants={rowVariants}
        initial="hidden"
        animate={inView ? "visible" : "hidden"}
      >
        <SkillsTrack 
          initial={{ x: baseX }}
          animate={inView ? trackAnimation : { x: baseX }}
        >
          {skills.map((skill, index) => (
            <IconWrapper 
              key={`${skill.name}-${index}`} 
              theme={theme}
              variants={iconVariants}
              whileHover="hover"
            >
              <i className={skill.iconClass}></i>
              <span className="skill-name">{skill.name}</span>
            </IconWrapper>
          ))}
        </SkillsTrack>
      </SkillsRow>
    );
  };

  return (
    <StyledSection id="skills" theme={theme}>
      <Container sx={{ paddingTop: '20px' }}>
        <MainTitle 
          ref={titleRef}
          theme={theme}
          variants={titleVariants}
          initial="hidden"
          animate={titleInView ? "visible" : "hidden"}
        >
          Tech Stack
        </MainTitle>

        <CategorySection>
          <CategoryTitle 
            ref={progRef}
            theme={theme} 
            variants={categoryVariants}
            initial="hidden"
            animate={progInView ? "visible" : "hidden"}
          >
            <GlowingText theme={theme}>
              Programming & Web Development
            </GlowingText>
          </CategoryTitle>
          {renderSkillRow(programming, 'left', progRef, progInView)}
        </CategorySection>

        <CategorySection>
          <CategoryTitle 
            ref={dbRef}
            theme={theme}
            variants={categoryVariants}
            initial="hidden"
            animate={dbInView ? "visible" : "hidden"}
          >
            <GlowingText theme={theme}>
              Databases & Tools
            </GlowingText>
          </CategoryTitle>
          {renderSkillRow(databases, 'right', dbRef, dbInView)}
        </CategorySection>

        <CategorySection>
          <CategoryTitle 
            ref={frameworkRef}
            theme={theme}
            variants={categoryVariants}
            initial="hidden"
            animate={frameworkInView ? "visible" : "hidden"}
          >
            <GlowingText theme={theme}>
              Frameworks, Cloud & Methodologies
            </GlowingText>
          </CategoryTitle>
          {renderSkillRow(frameworks, 'left', frameworkRef, frameworkInView)}
        </CategorySection>
      </Container>
    </StyledSection>
  );
}

export default Skills;
