import React, { useContext, useRef, useEffect, useState } from 'react';
import styled from 'styled-components';
import { Typography, Container } from '@mui/material';
import { ThemeContext } from '../context/ThemeContext';

const StyledSection = styled.section`
  padding: 100px 0;
  min-height: 100vh;
  background-color: ${props => props.theme.colors.background};
  transition: background-color 0.5s ease;
  position: relative;
`;

const SectionTitle = styled.h2`
  color: ${props => props.theme.colors.accent};
  font-size: clamp(26px, 5vw, 32px);
  font-weight: 600;
  margin-bottom: 60px;
  text-align: center;
  position: relative;

  &::after {
    content: '';
    position: absolute;
    bottom: -15px;
    left: 50%;
    transform: translateX(-50%);
    width: 80px;
    height: 4px;
    background: linear-gradient(to right, ${props => props.theme.colors.accent}, transparent);
    transition: background 0.5s ease;
  }
`;

const TimelineContainer = styled.div`
  max-width: 1000px;
  margin: 0 auto;
  position: relative;
  padding: 20px 0;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    bottom: 0;
    left: 50%;
    width: 3px;
    background-color: ${props => props.theme.colors.accent + '40'};
    transform: translateX(-50%);
    transition: background-color 0.5s ease;
  }
`;

// Active timeline line that grows as user scrolls
const ActiveLine = styled.div`
  position: absolute;
  top: 0;
  left: 50%;
  width: 3px;
  height: ${props => props.progress + '%'};
  background-color: ${props => props.theme.colors.accent};
  transform: translateX(-50%);
  transition: height 0.1s ease-out;
  z-index: 1;
`;

const TimelineItem = styled.div`
  padding: 10px 40px;
  position: relative;
  width: 50%;
  box-sizing: border-box;
  margin-bottom: 50px;

  /* Left side items */
  &:nth-child(odd) {
    left: 0;
    text-align: right;

    &::after {
      right: -9px;
    }
  }

  /* Right side items */
  &:nth-child(even) {
    left: 50%;
    text-align: left;

    &::after {
      left: -9px;
    }
  }

  /* Circle on timeline */
  &::after {
    content: '';
    position: absolute;
    top: 15px;
    width: 15px;
    height: 15px;
    border-radius: 50%;
    background-color: ${props => props.theme.colors.accent + '80'};
    transition: all 0.5s ease;
    z-index: 1;
    box-shadow: 0 0 0 4px ${props => props.theme.colors.background}, 
                0 0 0 6px ${props => props.theme.colors.accent + '40'};
  }
`;

// Radar blip animation for active point
const RadarBlip = styled.div`
  position: absolute;
  top: 7px;
  width: 30px;
  height: 30px;
  border-radius: 50%;
  background-color: transparent;
  z-index: 2;

  /* Position left or right based on even/odd item */
  ${props => props.position === 'left' ? `
    right: -17px;
  ` : `
    left: -17px;
  `}

  &::before {
    content: '';
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    width: 15px;
    height: 15px;
    border-radius: 50%;
    background-color: ${props => props.active ? props.theme.colors.accent : 'transparent'};
    box-shadow: 0 0 0 0 ${props => props.theme.colors.accent + '80'};
    animation: ${props => props.active ? 'radar-ping 2s infinite' : 'none'};
  }

  &::after {
    content: '';
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    width: 30px;
    height: 30px;
    border-radius: 50%;
    background-color: ${props => props.active ? props.theme.colors.accent + '30' : 'transparent'};
    animation: ${props => props.active ? 'radar-pulse 2s infinite' : 'none'};
  }

  @keyframes radar-ping {
    0% {
      box-shadow: 0 0 0 0 ${props => props.theme.colors.accent + '80'};
    }
    70% {
      box-shadow: 0 0 0 10px ${props => props.theme.colors.accent + '00'};
    }
    100% {
      box-shadow: 0 0 0 0 ${props => props.theme.colors.accent + '00'};
    }
  }

  @keyframes radar-pulse {
    0% {
      opacity: 1;
      transform: translate(-50%, -50%) scale(1);
    }
    100% {
      opacity: 0;
      transform: translate(-50%, -50%) scale(2);
    }
  }
`;

const TimelineContent = styled.div`
  padding: 20px;
  background-color: ${props => props.theme.colors.cardBg};
  border-radius: 8px;
  box-shadow: 0 5px 25px rgba(0, 0, 0, 0.1);
  transition: all 0.5s ease, background-color 0.5s ease;
  opacity: ${props => props.active ? 1 : 0.7};
  transform: ${props => props.active ? 'scale(1.05)' : 'scale(1)'};

  &:hover {
    transform: translateY(-5px) scale(1.05);
    box-shadow: 0 10px 25px rgba(0, 0, 0, 0.2);
  }
`;

const Role = styled.h3`
  color: ${props => props.theme.colors.text};
  margin: 0 0 5px 0;
  font-size: 1.2rem;
  font-weight: 700;
  transition: color 0.5s ease;
`;

const Company = styled.h4`
  color: ${props => props.theme.colors.accent};
  margin: 0 0 10px 0;
  font-size: 1rem;
  transition: color 0.5s ease;
`;

const Year = styled.div`
  position: absolute;
  top: 12px;
  font-size: 0.9rem;
  padding: 3px 10px;
  background-color: ${props => props.theme.colors.accent};
  color: ${props => props.theme.isDarkMode ? '#0a192f' : '#ffffff'};
  border-radius: 20px;
  font-weight: 500;
  box-shadow: 0 2px 5px rgba(0, 0, 0, 0.2);
  transition: all 0.5s ease;
  z-index: 2;

  /* Position left or right */
  ${props => props.position === 'left' ? `
    right: -105px;
  ` : `
    left: -105px;
  `}
`;

const Description = styled.p`
  color: ${props => props.theme.colors.secondaryText};
  font-size: 0.9rem;
  line-height: 1.6;
  margin: 0;
  transition: color 0.5s ease;
`;

// Array of experience items
const experienceItems = [
  {
    role: "Bachelor's",
    company: "Anna University",
    year: "2019",
    description: "Graduated with a Bachelor's in Computer Science, focusing on advanced software development and problem-solving."
  },
  {
    role: "Associate Software Engineer",
    company: "Accenture",
    year: "2021",
    description: "Designed and developed SaaS applications using React, AngularJS, Node.js, MongoDB, and RESTful APIs. Applied Agile methodologies and test-driven development to enhance stability and reliability. Optimized APIs, restructured database models, and collaborated with DevOps to streamline CI/CD pipelines, ensuring seamless deployment while maintaining version control and efficient code management in Git."
  },
  {
    role: "Software Engineer",
    company: "Accenture",
    year: "2021",
    description: "Developed Java backend features based on Jira requirements, integrating Windchill and Polarion tools with regular sprint updates. Optimized cloud infrastructure by testing AWS servers and monitoring Lambda functions. Enhanced deployment efficiency through automation while ensuring accurate user permissions and seamless documentation updates."
  },
  {
    role: "Master's Student",
    company: "Purdue University",
    year: "2024",
    description: "Currently pursuing a Master's in Computer Science, specializing in advanced software development and problem-solving. Working on Python projects, applying algorithms, data structures, and AI techniques. Gaining hands-on experience with cloud computing, system design, and scalable application development."
  }
];

function Experience() {
  const theme = useContext(ThemeContext);
  const timelineRef = useRef(null);
  const [progress, setProgress] = useState(0);
  const [activeItems, setActiveItems] = useState([]);

  // Track scroll progress to animate timeline
  useEffect(() => {
    const handleScroll = () => {
      if (!timelineRef.current) return;

      const timelineRect = timelineRef.current.getBoundingClientRect();
      const timelineTop = timelineRect.top;
      const timelineHeight = timelineRect.height;
      const viewportHeight = window.innerHeight;

      // Calculate progress percentage based on how much of the timeline is visible
      if (timelineTop > viewportHeight) {
        // Timeline is below viewport
        setProgress(0);
      } else if (timelineTop + timelineHeight < 0) {
        // Timeline is above viewport
        setProgress(100);
      } else {
        // Timeline is partially visible
        const visiblePortion = Math.min(
          viewportHeight, 
          timelineTop + timelineHeight
        ) - Math.max(0, timelineTop);

        const visiblePercentage = (visiblePortion / timelineHeight) * 100;
        const scrollPercentage = (Math.abs(timelineTop) / (timelineHeight - viewportHeight)) * 100;

        setProgress(Math.min(100, Math.max(0, scrollPercentage)));
      }

      // Determine which items are in view and should have active radar blips
      const items = document.querySelectorAll('.timeline-item');
      const activeItemsIndices = [];

      items.forEach((item, index) => {
        const rect = item.getBoundingClientRect();
        const isVisible = rect.top <= viewportHeight * 0.7 && rect.bottom >= viewportHeight * 0.3;

        if (isVisible) {
          activeItemsIndices.push(index);
        }
      });

      setActiveItems(activeItemsIndices);
    };

    window.addEventListener('scroll', handleScroll);
    // Call once to set initial state
    handleScroll();

    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <StyledSection id="experience" theme={theme}>
      <Container>
        <SectionTitle theme={theme}>MY CAREER & EXPERIENCE</SectionTitle>

        <TimelineContainer ref={timelineRef} theme={theme}>
          {/* Active progress line */}
          <ActiveLine progress={progress} theme={theme} />

          {experienceItems.map((item, index) => (
            <TimelineItem 
              key={index} 
              className="timeline-item"
              theme={theme}
            >
              <Year 
                position={index % 2 === 0 ? 'left' : 'right'} 
                theme={theme}
              >
                {item.year}
              </Year>

              {/* Radar blip effect */}
              <RadarBlip 
                position={index % 2 === 0 ? 'left' : 'right'}
                theme={theme}
                active={activeItems.includes(index)}
              />

              <TimelineContent 
                theme={theme} 
                active={activeItems.includes(index)}
              >
                <Role theme={theme}>{item.role}</Role>
                <Company theme={theme}>{item.company}</Company>
                <Description theme={theme}>{item.description}</Description>
              </TimelineContent>
            </TimelineItem>
          ))}
        </TimelineContainer>
      </Container>
    </StyledSection>
  );
}

export default Experience;
