import React, { useState, useEffect, useContext } from 'react';
import { AppBar, Toolbar, IconButton, Button, Box, Drawer, List, ListItem } from '@mui/material';
import MenuIcon from '@mui/icons-material/Menu';
import styled from 'styled-components';
import { Link } from 'react-scroll';
import { motion } from 'framer-motion';
import { ThemeContext } from '../context/ThemeContext';

// Using the ThemeContext to get theme colors
const StyledAppBar = styled(motion(AppBar))`
  background: ${props => props.theme.colors.navBackground};
  backdrop-filter: blur(10px);
  box-shadow: none;
  transition: transform 0.5s cubic-bezier(0.645, 0.045, 0.355, 1), 
              background-color 0.5s cubic-bezier(0.645, 0.045, 0.355, 1);
  height: 70px;
  transform: ${({ scrolled }) => (scrolled ? 'translateY(0)' : 'translateY(-100%)')};
`;

const NavLink = styled(Link)`
  color: ${props => props.theme.colors.text};
  margin: 0 20px;
  padding: 10px;
  text-decoration: none;
  font-size: 13px;
  font-family: 'SF Mono', 'Fira Code', monospace;
  cursor: pointer;
  transition: all 0.25s cubic-bezier(0.645, 0.045, 0.355, 1);
  position: relative;

  &:hover {
    color: ${props => props.theme.colors.accent};
  }

  &::after {
    content: '';
    position: absolute;
    width: 0;
    height: 2px;
    bottom: 0;
    left: 0;
    background-color: ${props => props.theme.colors.accent};
    transition: width 0.3s ease;
  }

  &:hover::after {
    width: 100%;
  }
`;

const ResumeButton = styled(motion(Button))`
  color: ${props => props.theme.colors.accent};
  border: 1px solid ${props => props.theme.colors.accent};
  border-radius: 4px;
  padding: 0.75rem 1rem;
  font-size: 13px;
  font-family: 'SF Mono', 'Fira Code', monospace;
  line-height: 1;
  text-decoration: none;
  margin-left: 15px;
  overflow: hidden;
  position: relative;
  z-index: 1;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background-color: ${props => props.theme.colors.accent};
    transform: translateX(-100%);
    transition: transform 0.3s ease;
    z-index: -1;
  }

  &:hover {
    color: ${props => props.theme.isDarkMode ? '#0a192f' : '#ffffff'};
    box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
  }

  &:hover::before {
    transform: translateX(0);
  }
`;

const LogoImage = styled(motion.img)`
  height: 42px;
  width: 42px;
  border-radius: 50%;
  object-fit: cover;
  transition: transform 0.3s ease;

  &:hover {
    transform: rotate(360deg);
  }
`;

const Navbar = () => {
  const [mobileOpen, setMobileOpen] = useState(false);
  const [scrolled, setScrolled] = useState(false);
  const theme = useContext(ThemeContext);

  // Navbar animation variants
  const navbarVariants = {
    hidden: { y: -100 },
    visible: { 
      y: 0,
      transition: { 
        type: 'spring', 
        stiffness: 100, 
        damping: 15 
      }
    }
  };

  // Nav items animation variants
  const navItemVariants = {
    hidden: { opacity: 0, y: -20 },
    visible: i => ({
      opacity: 1,
      y: 0,
      transition: {
        delay: i * 0.1,
        duration: 0.5,
      }
    })
  };

  useEffect(() => {
    const handleScroll = () => {
      const threshold = window.innerHeight * 0.9; // Adjust threshold if needed
      const isScrolled = window.scrollY > threshold;
      if (isScrolled !== scrolled) {
        setScrolled(isScrolled);
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, [scrolled]);

  const handleDrawerToggle = () => {
    setMobileOpen(!mobileOpen);
  };

  const menuItems = ['About', 'Experience', 'Work', 'Contact'];

  const drawer = (
    <Box onClick={handleDrawerToggle} sx={{ 
      textAlign: 'center', 
      backgroundColor: theme.colors.secondaryBg,
      height: '100%',
      transition: 'background-color 0.5s ease'
    }}>
      <List>
        {menuItems.map((item, index) => (
          <ListItem key={item}>
            <NavLink
              to={item.toLowerCase()=== 'work' ? 'projects' : item.toLowerCase()}
              spy={true}
              smooth={true}
              offset={-70}
              duration={500}
              theme={theme}
            >
              {item}
            </NavLink>
          </ListItem>
        ))}
        <ListItem>
          <a href="/resume.pdf" target="_blank" rel="noopener noreferrer" style={{ textDecoration: 'none' }}>
            <ResumeButton variant="outlined" theme={theme}>Resume</ResumeButton>
          </a>
        </ListItem>
      </List>
    </Box>
  );

  return (
    <>
      <StyledAppBar 
        scrolled={scrolled} 
        position="fixed" 
        theme={theme}
        variants={navbarVariants}
        initial="hidden"
        animate={scrolled ? 'visible' : 'hidden'}
      >
        <Toolbar sx={{ justifyContent: 'space-between' }}>
          <Box component="a" href="/" sx={{ display: 'flex', alignItems: 'center' }}>
            <LogoImage
              src="/images/kkk.jpeg"
              alt="Logo"
              initial={{ opacity: 0, scale: 0 }}
              animate={{ opacity: 1, scale: 1 }}
              transition={{ type: 'spring', stiffness: 260, damping: 20 }}
            />
          </Box>

          <Box sx={{ display: { xs: 'none', md: 'flex' }, alignItems: 'center' }}>
            {menuItems.map((item, index) => (
              <motion.div
                key={item}
                custom={index}
                variants={navItemVariants}
                initial="hidden"
                animate="visible"
              >
                <NavLink
                  to={item.toLowerCase()}
                  spy={true}
                  smooth={true}
                  offset={-70}
                  duration={500}
                  theme={theme}
                >
                  {item}
                </NavLink>
              </motion.div>
            ))}
            <motion.div
              custom={menuItems.length}
              variants={navItemVariants}
              initial="hidden"
              animate="visible"
            >
              <a href="/KalyankumarKonduru.pdf" target="_blank" rel="noopener noreferrer" style={{ textDecoration: 'none' }}>
                <ResumeButton 
                  variant="outlined" 
                  theme={theme}
                  whileHover={{ scale: 1.05 }}
                  whileTap={{ scale: 0.95 }}
                >
                  Resume
                </ResumeButton>
              </a>
            </motion.div>
          </Box>

          <IconButton
            color="inherit"
            aria-label="open drawer"
            edge="start"
            onClick={handleDrawerToggle}
            sx={{ display: { md: 'none' } }}
          >
            <MenuIcon />
          </IconButton>
        </Toolbar>
      </StyledAppBar>

      <Box component="nav">
        <Drawer
          variant="temporary"
          anchor="right"
          open={mobileOpen}
          onClose={handleDrawerToggle}
          ModalProps={{
            keepMounted: true,
          }}
          sx={{
            display: { xs: 'block', md: 'none' },
            '& .MuiDrawer-paper': { 
              boxSizing: 'border-box', 
              width: 240,
              backgroundColor: theme.colors.secondaryBg,
              color: theme.colors.text,
              transition: 'background-color 0.5s ease, color 0.5s ease'
            },
          }}
        >
          {drawer}
        </Drawer>
      </Box>
    </>
  );
};

export default Navbar;
