import React, { useContext, useRef, useEffect, useState } from 'react';
import styled from 'styled-components';
import { Container } from '@mui/material';
import { ThemeContext } from '../context/ThemeContext';

const StyledSection = styled.section`
  padding: 100px 0;
  min-height: 100vh;
  background-color: ${props => props.theme.colors.background};
  transition: background-color 0.5s ease;
  position: relative;
`;

const SectionTitle = styled.h2`
  color: ${props => props.theme.colors.accent};
  font-size: clamp(26px, 5vw, 32px);
  font-weight: 600;
  margin-bottom: 60px;
  text-align: center;
  position: relative;

  &::after {
    content: '';
    position: absolute;
    bottom: -15px;
    left: 50%;
    transform: translateX(-50%);
    width: 80px;
    height: 4px;
    background: linear-gradient(to right, ${props => props.theme.colors.accent}, transparent);
    transition: background 0.5s ease;
  }
`;

// Timeline container with all elements
const TimelineContainer = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  position: relative;
`;

// Orange vertical line
const TimelineLine = styled.div`
  position: absolute;
  top: 0;
  bottom: 0;
  right: 50%;
  width: 2px;
  background-color: ${props => props.theme.colors.accent};
  z-index: 1;
  transition: all 0.3s ease;
`;

// Content container with alternating layout
const TimelineContent = styled.div`
  padding-bottom: 100px;
  position: relative;
  width: 100%;

  &:nth-child(even) {
    .card {
      margin-left: auto;
      border-right: 4px solid ${props => props.active ? props.theme.colors.accent : 'transparent'};
      text-align: right;
    }
    .year-badge {
      left: 50%;
      margin-left: 20px;
    }
  }

  &:nth-child(odd) {
    .card {
      margin-right: auto;
      border-left: 4px solid ${props => props.active ? props.theme.colors.accent : 'transparent'};
      text-align: left;
      margin-left: 50%;
    }
    .year-badge {
      right: 50%;
      margin-right: 20px;
    }
  }
`;

// Timeline dot with radar effect
const TimelineDot = styled.div`
  position: absolute;
  top: 20px;
  right: calc(50% - 10px);
  width: 20px;
  height: 20px;
  border-radius: 50%;
  background-color: ${props => props.active ? props.theme.colors.accent : '#fff'};
  border: 2px solid ${props => props.theme.colors.accent};
  z-index: 2;
  transition: background-color 0.3s ease;

  ${props => props.active && `
    &::before, &::after {
      content: '';
      position: absolute;
      border-radius: 50%;
      top: 50%;
      left: 50%;
      transform: translate(-50%, -50%);
      background-color: transparent;
      border: 2px solid ${props.theme.colors.accent};
      animation: radar 2s infinite;
      opacity: 0;
    }

    &::before {
      width: 200%;
      height: 200%;
      animation-delay: 0s;
    }

    &::after {
      width: 300%;
      height: 300%;
      animation-delay: 0.5s;
    }
  `}

  @keyframes radar {
    0% {
      opacity: 1;
      transform: translate(-50%, -50%) scale(0.5);
    }
    100% {
      opacity: 0;
      transform: translate(-50%, -50%) scale(1.5);
    }
  }
`;

// The "circle" connector that contains the dot
const TimelineCircle = styled.div`
  position: absolute;
  top: 15px;
  right: calc(50% - 15px);
  width: 30px;
  height: 30px;
  border-radius: 50%;
  background-color: ${props => props.theme.isDarkMode ? props.theme.colors.background : '#fff'};
  border: 2px solid ${props => props.theme.colors.accent};
  z-index: 1;
`;

// Year badge
const YearBadge = styled.div`
  position: absolute;
  top: 15px;
  background-color: ${props => props.theme.colors.accent};
  color: white;
  padding: 5px 15px;
  border-radius: 20px;
  font-weight: 500;
  z-index: 3;
  transition: all 0.3s ease;
`;

// Content card
const Card = styled.div`
  width: 45%;
  background-color: ${props => props.theme.isDarkMode ? '#112240' : 'white'};
  border-radius: 10px;
  padding: 30px;
  box-shadow: 0 4px 20px rgba(0, 0, 0, 0.1);
  transition: all 0.3s ease;
  transform: ${props => props.active ? 'translateY(-5px)' : 'none'};

  &:hover {
    transform: translateY(-5px);
    box-shadow: 0 10px 30px rgba(0, 0, 0, 0.15);
  }
`;

const Role = styled.h3`
  color: ${props => props.theme.colors.text};
  margin: 0 0 5px 0;
  font-size: 24px;
  font-weight: 600;
`;

const Company = styled.h4`
  color: ${props => props.theme.colors.accent};
  margin: 0 0 15px 0;
  font-size: 18px;
`;

const Description = styled.p`
  color: ${props => props.theme.colors.secondaryText};
  font-size: 16px;
  line-height: 1.6;
  margin: 0;
`;

// Experience items data
const experienceItems = [
  {
    role: "Bachelor's",
    company: "Anna University",
    year: "2019",
    description: "Graduated with a Bachelor's in Computer Science, focusing on advanced software development and problem-solving."
  },
  {
    role: "Associate Software Engineer",
    company: "Accenture",
    year: "2021",
    description: "Designed and developed SaaS applications using React, AngularJS, Node.js, MongoDB, and RESTful APIs. Applied Agile methodologies and test-driven development to enhance stability and reliability. Optimized APIs, restructured database models, and collaborated with DevOps to streamline CI/CD pipelines, ensuring seamless deployment while maintaining version control and efficient code management in Git."
  },
  {
    role: "Software Engineer",
    company: "Accenture",
    year: "2021",
    description: "Developed Java backend features based on Jira requirements, integrating Windchill and Polarion tools with regular sprint updates. Optimized cloud infrastructure by testing AWS servers and monitoring Lambda functions. Enhanced deployment efficiency through automation while ensuring accurate user permissions and seamless documentation updates."
  },
  {
    role: "Master's Student",
    company: "Purdue University",
    year: "2024",
    description: "Currently pursuing a Master's in Computer Science, specializing in advanced software development and problem-solving. Working on Python projects, applying algorithms, data structures, and AI techniques. Gaining hands-on experience with cloud computing, system design, and scalable application development."
  }
];

function Experience() {
  const theme = useContext(ThemeContext);
  const containerRef = useRef(null);
  const [activeIndex, setActiveIndex] = useState(0);

  // Handle scroll to detect active timeline item
  useEffect(() => {
    const handleScroll = () => {
      if (!containerRef.current) return;

      const items = document.querySelectorAll('.timeline-item');
      let newActiveIndex = 0;

      items.forEach((item, index) => {
        const rect = item.getBoundingClientRect();
        const centerY = window.innerHeight / 2;

        // If the item's dot is near the center of the viewport, mark it as active
        if (rect.top <= centerY && rect.bottom >= centerY) {
          newActiveIndex = index;
        }
      });

      setActiveIndex(newActiveIndex);
    };

    window.addEventListener('scroll', handleScroll);
    handleScroll(); // Initial call

    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <StyledSection id="experience" theme={theme}>
      <Container maxWidth="lg">
        <SectionTitle theme={theme}>MY CAREER & EXPERIENCE</SectionTitle>

        <TimelineContainer ref={containerRef}>
          <TimelineLine theme={theme} />

          {experienceItems.map((item, index) => (
            <TimelineContent
              key={index}
              className="timeline-item"
              theme={theme}
              active={index === activeIndex}
            >
              <YearBadge theme={theme} className="year-badge">
                {item.year}
              </YearBadge>

              <TimelineCircle theme={theme} />
              <TimelineDot active={index === activeIndex} theme={theme} />

              <Card
                className="card"
                theme={theme}
                active={index === activeIndex}
              >
                <Role theme={theme}>{item.role}</Role>
                <Company theme={theme}>{item.company}</Company>
                <Description theme={theme}>{item.description}</Description>
              </Card>
            </TimelineContent>
          ))}
        </TimelineContainer>
      </Container>
    </StyledSection>
  );
}

export default Experience;
