import React, { useContext, useRef, useEffect } from 'react';
import styled from 'styled-components';
import { useInView } from 'react-intersection-observer';
import { Typography, Container, Box } from '@mui/material';
import { ThemeContext } from '../context/ThemeContext';

const StyledSection = styled.section`
  padding: 100px 0;
  min-height: 100vh;
  background-color: ${props => props.theme.colors.background};
  transition: background-color 0.5s ease;
  position: relative;
`;

const SectionTitle = styled.h2`
  color: ${props => props.theme.colors.accent};
  font-size: clamp(26px, 5vw, 32px);
  font-weight: 600;
  margin-bottom: 60px;
  text-align: center;
  position: relative;

  &::after {
    content: '';
    position: absolute;
    bottom: -15px;
    left: 50%;
    transform: translateX(-50%);
    width: 80px;
    height: 4px;
    background: linear-gradient(to right, ${props => props.theme.colors.accent}, transparent);
    transition: background 0.5s ease;
  }
`;

const TimelineContainer = styled.div`
  max-width: 1000px;
  margin: 0 auto;
  position: relative;
  padding: 20px 0;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    bottom: 0;
    left: 50%;
    width: 3px;
    background-color: ${props => props.theme.colors.accent};
    transform: translateX(-50%);
    transition: background-color 0.5s ease;
  }
`;

const TimelineItem = styled.div`
  padding: 10px 40px;
  position: relative;
  width: 50%;
  box-sizing: border-box;
  margin-bottom: 50px;

  /* Left side items */
  &:nth-child(odd) {
    left: 0;
    text-align: right;

    &::after {
      right: -6px;
    }
  }

  /* Right side items */
  &:nth-child(even) {
    left: 50%;
    text-align: left;

    &::after {
      left: -6px;
    }
  }

  /* Circle on timeline */
  &::after {
    content: '';
    position: absolute;
    top: 15px;
    width: 15px;
    height: 15px;
    border-radius: 50%;
    background-color: ${props => props.theme.colors.accent};
    transition: background-color 0.5s ease;
    z-index: 1;
    box-shadow: 0 0 0 4px ${props => props.theme.colors.background}, 
                0 0 0 6px ${props => props.theme.colors.accent + '40'};
  }

  /* Highlight item when in view */
  &.in-view::after {
    background-color: ${props => props.theme.colors.highlight};
    box-shadow: 0 0 0 4px ${props => props.theme.colors.background}, 
                0 0 0 8px ${props => props.theme.colors.highlight + '40'};
    transform: scale(1.2);
    transition: all 0.5s ease;
  }
`;

const TimelineContent = styled.div`
  padding: 20px;
  background-color: ${props => props.theme.colors.cardBg};
  border-radius: 8px;
  box-shadow: 0 5px 25px rgba(0, 0, 0, 0.1);
  transition: all 0.5s ease, background-color 0.5s ease;

  &:hover {
    transform: translateY(-5px);
    box-shadow: 0 10px 25px rgba(0, 0, 0, 0.2);
  }
`;

const Role = styled.h3`
  color: ${props => props.theme.colors.text};
  margin: 0 0 5px 0;
  font-size: 1.2rem;
  font-weight: 700;
  transition: color 0.5s ease;
`;

const Company = styled.h4`
  color: ${props => props.theme.colors.accent};
  margin: 0 0 10px 0;
  font-size: 1rem;
  transition: color 0.5s ease;
`;

const Year = styled.div`
  position: absolute;
  top: 12px;
  font-size: 0.9rem;
  padding: 3px 10px;
  background-color: ${props => props.theme.colors.accent};
  color: ${props => props.theme.isDarkMode ? '#0a192f' : '#ffffff'};
  border-radius: 20px;
  font-weight: 500;
  box-shadow: 0 2px 5px rgba(0, 0, 0, 0.2);
  transition: background-color 0.5s ease, color 0.5s ease;

  /* Position left or right */
  ${props => props.side === 'left' ? `
    right: -105px;
  ` : `
    left: -105px;
  `}
`;

const Description = styled.p`
  color: ${props => props.theme.colors.secondaryText};
  font-size: 0.9rem;
  line-height: 1.6;
  margin: 0;
  transition: color 0.5s ease;
`;

// Array of experience items
const experienceItems = [
  {
    role: "Bachelor's",
    company: "Anna University",
    year: "2019",
    description: "Graduated with a Bachelor's in Computer Science, focusing on advanced software development and problem-solving."
  },
  {
    role: "Associate Software Engineer",
    company: "Accenture",
    year: "2021",
    description: "Designed and developed SaaS applications using React, AngularJS, Node.js, MongoDB, and RESTful APIs. Applied Agile methodologies and test-driven development to enhance stability and reliability. Optimized APIs, restructured database models, and collaborated with DevOps to streamline CI/CD pipelines, ensuring seamless deployment while maintaining version control and efficient code management in Git."
  },
  {
    role: "Software Engineer",
    company: "Accenture",
    year: "2021",
    description: "Developed Java backend features based on Jira requirements, integrating Windchill and Polarion tools with regular sprint updates. Optimized cloud infrastructure by testing AWS servers and monitoring Lambda functions. Enhanced deployment efficiency through automation while ensuring accurate user permissions and seamless documentation updates."
  },
  {
    role: "Master's Student",
    company: "Purdue University",
    year: "2024",
    description: "Currently pursuing a Master's in Computer Science, specializing in advanced software development and problem-solving. Working on Python projects, applying algorithms, data structures, and AI techniques. Gaining hands-on experience with cloud computing, system design, and scalable application development."
  }
];

function Experience() {
  const theme = useContext(ThemeContext);
  const [ref, inView] = useInView({
    triggerOnce: false,
    threshold: 0.2,
  });

  // Animate timeline progress
  const progressRef = useRef(null);

  useEffect(() => {
    const handleScroll = () => {
      if (!progressRef.current) return;

      const timelineItems = document.querySelectorAll('.timeline-item');

      timelineItems.forEach(item => {
        const rect = item.getBoundingClientRect();
        const isVisible = (rect.top <= window.innerHeight * 0.8 && rect.bottom >= window.innerHeight * 0.2);

        if (isVisible) {
          item.classList.add('in-view');
        } else {
          item.classList.remove('in-view');
        }
      });
    };

    window.addEventListener('scroll', handleScroll);
    // Call once to set initial state
    handleScroll();

    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <StyledSection id="experience" theme={theme}>
      <Container>
        <SectionTitle theme={theme}>MY CAREER & EXPERIENCE</SectionTitle>

        <TimelineContainer ref={progressRef} theme={theme}>
          {experienceItems.map((item, index) => (
            <TimelineItem 
              key={index} 
              className="timeline-item"
              theme={theme}
            >
              <Year 
                side={index % 2 === 0 ? 'left' : 'right'} 
                theme={theme}
              >
                {item.year}
              </Year>
              <TimelineContent theme={theme}>
                <Role theme={theme}>{item.role}</Role>
                <Company theme={theme}>{item.company}</Company>
                <Description theme={theme}>{item.description}</Description>
              </TimelineContent>
            </TimelineItem>
          ))}
        </TimelineContainer>
      </Container>
    </StyledSection>
  );
}

export default Experience;
